import { Component } from '@angular/core';
import { TrainerService } from 'src/app/services/trainer.service';

@Component({
  selector: 'app-trainer-login',
  templateUrl: './trainer-login.component.html',
  styleUrls: ['./trainer-login.component.css']
})
export class TrainerLoginComponent {
  showLogin = true;
  showForgetSection = true;
  email: any;
  password: any;
  msg = true;
  loginError = true;
  confirmPassword= true;

  member: Member = {
    memberId: 0,
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    gender: '',
    mobileNo: '',
    forgetPasswordAnswer: '',
    confirmPassword: ''
  };

  constructor(private trainerService: TrainerService, private router: Router) {}

  ngOnInit() {
    if (localStorage.length > 0) {
      this.setLocalStorage();
      this.onSubmit();
    }
  }

  onSubmit() {
    this.trainerService.loginMember(this.member).subscribe({
      next: (value) => {
        localStorage.setItem('email', this.member.email);
        localStorage.setItem('password', this.member.password);
        this.trainerService.serviceMemberData = value;
        this.router.navigate(['member']);
      },
      error: (err) => {
        this.loginError = false
      },
    });
  }

  onCheck() {
    this.trainerService.checkForgetPassword(this.member).subscribe({
      next: (value) => {
        this.showForgetSection = false;
        this.member.password = "";
      },
      error: (err) => {
        this.msg = false;
      },
    });
  }

  onChangePassword() {
    if (this.member.password.match(this.member.confirmPassword)) {
      this.trainerService.updatePassword(this.member).subscribe({
        next: (value) => {
          this.showLogin = true
          this.member.email = ""
          this.member.password = ""
        },
        error: (err) => {
        },
      });
    }
    else{
      this.confirmPassword = false
    }
  }

  setLocalStorage() {
    this.email = localStorage.getItem('email');
    this.password = localStorage.getItem('password');
    this.member.email = this.email.toString();
    this.member.password = this.password.toString();
  }
}
