import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { Trainer } from 'src/app/entity/trainer';
import { TrainerService } from 'src/app/services/trainer.service';

@Component({
  selector: 'app-trainer-register',
  templateUrl: './trainer-register.component.html',
  styleUrls: ['./trainer-register.component.css']
})
export class TrainerRegisterComponent {
  showSecound = false;
  loginError= false;

  trainer: Trainer = {

  };

  constructor(private memberService: TrainerService, private router: Router) {}

  onSubmit() {
      this.memberService.insertTrainer(this.member).subscribe({
        next:(value)=> {
          console.log(value)
            this.router.navigate(['/login'])
        },
        error:(err)=>{
            console.log(err)
        },
      })    
  }

  onNext() {
   this.memberService.checkMemberEmail(this.member).subscribe({
    next:(value)=>{
       if(value){
        this.showSecound= true;
      }
      // alert("The Email You Enter That Already Exits!!")
      this.loginError = true
    },
    error:(err)=>{
      this.showSecound  = true
        console.log(err)
    },
   })


  }
}
