import { Component } from '@angular/core';
import { Member } from '../entity/member';
import { MemberService } from '../services/member.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  showForgetPasswordContent = false;
  memberData: Member = {
    email: '',
    password: '',
    forgetPasswordAnswer: '',
    memberId: 0,
    firstName: '',
    lastName: '',
    mobileNo: '',
    gender: ''
  }

  constructor(private memberService: MemberService, private router: Router) { }


  onSubmit() {
    this.memberService.loginMember(this.memberData).subscribe({
      next: (response) => {
        this.memberService.serviceMemberData = response
        console.log("nest : ", response)
        console.log("assign : ", this.memberService.serviceMemberData)
      },
      error: (err) => {
        console.log("error : ", err)
      },
    })
  }

  showForgetPassword() {
    this.showForgetPasswordContent = true;
  }
}
